mapper.update:
UPDATE product
SET
    
        product_name = #{productName}
    
    
        , product_category = #{productCategory}
    
    
        , address = #{address}
    
    
        , address_detail = #{addressDetail}
    
    
        , zip_code = #{zipCode}
    
    
        , tel_num = #{telNum}
    
WHERE product_id = #{productId}
